import React from 'react';
import Prompt from '../Prompt/Prompt'
import AnswerBox from '../AnswerBox/AnswerBox';

function SurveyBox(props){
    const response = 'meep'
    setTimeout(() => {
        props.setResponseBank(response, props.currentQuestion)
    }, 100)
    // console.log(props.responseBank)

    const number = props.questionBank[props.currentQuestion]["question-id"]
    const prompt = props.questionBank[props.currentQuestion]["question"]

    return (
        <div>
            <Prompt number={number} prompt={prompt} />
            <AnswerBox />
        </div>
    )
}

export default SurveyBox;
